CustomField.formula:
IF( MONTH(TODAY()) = 12,
      " et de très belles fêtes de fin d’année",
   IF( AND (MONTH(Birthdate) = MONTH(TODAY()), DAY(Birthdate) = DAY(TODAY())), 
         " et un joyeux anniversaire",
      IF( AND( TODAY() >= DATE(YEAR(TODAY()), 1, 1), TODAY() <= DATE(YEAR(TODAY()), 1, 15)),
            " et nos meilleurs vœux pour l’année" & TEXT(YEAR(TODAY())),
         IF( WEEKDAY(TODAY()) = 6,
            " et un très bon week-end",
            ""
         )
      )
   )
)
